import jsonimport requestsclass SendingMusic:    def __init__(self, file_name):        self.file_name = file_name        self.headers = {"Content-Type": "application/json"}        self.url = "http://usd-prog5:3000/push"    def sending(self):        reqs = []        time = -1        with open(self.file_name, 'r', encoding='utf-8') as file:            for line in file:                if line == '\n':                    continue                line_split = line.split('|')                if time != int(line_split[0]):                    if time != -1:                        requests.post(self.url, headers=self.headers,data=json.dumps(reqs[len(reqs) - 10:]))                        time.sleep(1)                        reqs = []                    time = int(line_split[0])                else:                    reqs.append({                        "amount": len(reqs),                        "code": line_split[3],                        "size": line_split[4]                })        requests.post(url=self.url, headers=self.headers, data=json.dumps(reqs[len(reqs) - 10:]))